public class Trianglepattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n =5;
		for (int i = 0; i <n; i++) {
			for(int coloumn =0; coloumn<=i; coloumn++) {
				System.out.print("$ ");
			}
			System.out.println( );

		}
		
	}

}
